mapper.select:
SELECT U.USER_NO, U.USER_NICKNAME, U.USER_ADDRESS, U.USER_LATITUDE, U.USER_LONGITUDE, PS.PRO_PHOTO FROM TBL_USER U, TBL_PETSITTER_PROFILE PS WHERE 1=1 AND U.USER_NO = PS.USER_NO AND U.USER_STATUS = 'N' AND U.USER_LATITUDE IS NOT NULL /*좌표필수*/ AND U.USER_LONGITUDE IS NOT NULL /*좌표필수*/
AND EXISTS( SELECT 1 FROM TBL_SERVICE_PETKINDS WHERE 1=1 AND U.USER_NO = USER_NO AND USE_YN = 'Y'
AND PET_TYPE_CODE = #{dogType}
AND PET_TYPE_CODE = #{petType}
)
AND EXISTS( SELECT 1 FROM TBL_SERVICE_TYPE WHERE 1=1 AND U.USER_NO = USER_NO AND USE_YN = 'Y'
AND SERVICE_TYPE_CODE IN
#{item}
AND PRICE BETWEEN #{minPrice} AND #{maxPrice}
)
AND EXISTS( SELECT 1 FROM TBL_SERVICE_CALENDAR WHERE 1=1 AND U.USER_NO = USER_NO
AND SERVICE_DATE >= TO_DATE(#{startDate},'YYYY-MM-DD')
AND SERVICE_DATE <= TO_DATE(#{endDate},'YYYY-MM-DD')
)
AND EXISTS( SELECT 1 FROM TBL_PETSITTER_SKILL WHERE 1=1 AND U.USER_NO = USER_NO AND USE_YN = 'Y' AND SKILL_TYPE_CODE IN
#{item}
)
AND REPLACE(U.USER_ADDRESS,' ','') LIKE REPLACE(#{addrSidoNm}||#{addrSigugunNm}||#{addrDongNm},' ','')||'%'
