mapper.select:
SELECT user_id, user_pw
FROM tbl_user
WHERE user_id = #{user_id}
AND user_pw = #{user_pw}

mapper.update:
		UPDATE 
    		tbl_user
		SET
		    user_pw = #{user_pw}
		    user_nickname = #{user_nickname}
		    user_contact = #{user.contact}
		    user_address = #{user_address}
		    mod_user_no = #{user_no}
		    mod_date = sysdate
		WHERE
			user_no
