mapper.select:
SELECT user_id, user_pw
FROM tbl_user
WHERE user_id = #{userId}
AND user_pw = #{userPw}

mapper.update:
		UPDATE 
    		tbl_user
		SET
		    user_pw = #{userPw}
		    user_nickname = #{userNickname}
		    user_contact = #{userContact}
		    user_address = #{userAddress}
		    mod_user_no = #{userNo}
		    mod_date = sysdate
		WHERE
			user_no
